mapper.insert:


	SELECT
		CONCAT('GOODS-', LPAD(COUNT(id) + 1, 5, 0)) AS code
	FROM
		goods

INSERT INTO goods (

) VALUES (
	  #{id}
	, #{code}
	, #{name}
	, #{description}
	, #{price}
	, IFNULL(#{status}, 'NORMAL')
	, IFNULL(#{rate}, 0)
	, 'N'
	, NOW()
	, NULL
)

mapper.select:
SELECT
FROM goods
delete_yn = 'N' AND code = #{value}

mapper.update:
UPDATE goods
SET
	modified_date = NOW()

	, name = #{name}


	, description = #{description}


	, price = #{price}


	, rate = #{rate}


	, status = #{status}

WHERE
	code = #{code}

mapper.select:
SELECT COUNT(*) FROM goods
delete_yn = 'N'
AND status = #{status}
